import React, { useEffect, useState } from 'react';
import classes from './ProductDetail.module.css';
//import classes from '../../Pages/ProductDetail/ProductDetail.module.css'
import LayOut from '../../Components/LayOut/LayOut';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { productUrl } from '../../Api/endPoints';
import ProductCard from '../../Components/Product/ProductCard';
import Loader from '../../Components/Loader/Loader';

function ProductDetail() {
  const { productId } = useParams();
  const [isLoading, setIsLoading] = useState(true);
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const fetchProduct = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get(`${productUrl}/products/${productId}`);
        console.log("API Response:", response.data); // Log the response data

        // Check if the product data structure is correct
        if (response.data) {
          setProduct(response.data);
        } else {
          console.error("No product data found");
        }
      } catch (error) {
        console.error("Error fetching product:", error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchProduct();
  }, [productId]);

  return (
    <LayOut>
      {isLoading ? (
        <Loader />
      ) : product ? (
        <ProductCard
          product={product} 
          flex={true}
          renderDesc={true}
        />
      ) : (
        <p>Product not found.</p> // Handle case where product is null
      )}
    </LayOut>
  );
}

export default ProductDetail;
