import React from 'react';
import { Carousel as ResponsiveCarousel } from 'react-responsive-carousel'; // Import the carousel component
import { img } from './img/data'; // Import images data
import "react-responsive-carousel/lib/styles/carousel.min.css"; // Import carousel styles
import styles from './carousel.module.css'; // Import your custom styles if you have them

function Carousel() {
    return (
        <div className={styles.carouselContainer}>
            <ResponsiveCarousel
                autoPlay={true}
                infiniteLoop={true}
                showIndicators={true}
                showThumbs={false}
                showStatus={false} // Hides the status text (current slide)
                interval={3000} // Time between slides in milliseconds
                transitionTime={500} // Time for the transition effect
            >
                {
                    img.map((imageItemLink, index) => (
                        <div key={index}>
                            <img src={imageItemLink} alt={`Slide ${index}`} />
                        </div>
                    ))
                }
            </ResponsiveCarousel>
        </div>
    );
}

export default Carousel;
