import React from 'react';
import CatagoryFullinfos from './CatagoryFullinfos'; // Adjust the path as necessary
import CatagoryCard from './CatagoryCard'; // Capitalize the component name
import { makeStyles } from '@material-ui/core/styles'; // Import makeStyles if using Material-UI

const useStyles = makeStyles((theme) => ({
  catagory_container: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      padding: theme.spacing(2),
      backgroundColor: theme.palette.background.paper,
  },
  catagory_card: {
      margin: theme.spacing(1),
      width: '300px',
      border: '1px solid #ccc',
      borderRadius: '8px',
      boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)',
      '& img': {
          width: '100%',
          borderRadius: '8px 8px 0 0',
      },
  },
}));

function Catagory() {
    const classes = useStyles(); // Define classes

    return (
        <section className={classes.catagory_container}>
            {CatagoryFullinfos.map((infos) => (
                <CatagoryCard key={infos.title} data={infos} /> // Return the component correctly
            ))}
        </section>
    );
}

export default Catagory;
