import classes from './ProductDetail.module.css';
import LayOut from '../../Components/LayOut/LayOut'; // Correct path to LayOut component
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { productUrl } from '../../Api/endPoints';
import ProductCard from '../../Components/Product/ProductCard'; // Adjust based on your actual file structure
import Loader from '../../Components/loader/Loader'; // Ensure loader file path is correct

function ProductDetail() {
  const { productId } = useParams();
  const [product, setProduct] = useState({}); // Correctly initialized state
  
  useEffect(() => {
    axios.get(`${productUrl}/products/${productId}`) // Use backticks for string interpolation
      .then((res) => {
        setProduct(res.data); // Set the product data
        
      })
      .catch((err) => {
        console.log(err);
       
      });
  }, []); // Add productId as a dependency

 

  return (
    <LayOut>
      
    </LayOut>
  );
}

export default ProductDetail;
