import React, { useEffect, useState } from 'react';
import classes from './ProductDetail.module.css';
import LayOut from '../../Components/LayOut/LayOut';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { productUrl } from '../../Api/endPoints';
import ProductCard from '../../Components/Product/ProductCard';
import Loader from '../../Components/Loader/Loader';

function ProductDetail() {
  const { productId } = useParams();
  const [isLoading, setIsLoading] = useState(true);
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const fetchProduct = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get(`${productUrl}/products/${productId}`);
        console.log(response.data); // Use response instead of res
        setProduct(response.data);
      } catch (err) {
        console.error(err);
      } finally {
        setIsLoading(false); // Ensure loading state is set to false in all cases
      }
    };

    fetchProduct();
  }, [productId]); // Add productId to the dependency array

  return (
    <LayOut>
      {isLoading ? (
        <Loader />
      ) : product ? (
        <ProductCard
          product={product}
          flex={true}
          renderDesc={true}
        />
      ) : (
        <p>Product not found.</p> // Optional handling for when product is null
      )}
    </LayOut>
  );
}

export default ProductDetail;
