import React, { useEffect, useState } from 'react';
import classes from './ProductDetail.module.css';
import LayOut from '../../Components/LayOut/LayOut'; // Correct path to LayOut component
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { productUrl } from '../../Api/endPoints';
import ProductCard from '../../Components/Product/ProductCard'; // Adjust based on your actual file structure
import Loader from '../../Components/Loader/Loader'; // Ensure this path is correct

function ProductDetail() {
  const { productId } = useParams();
  const [isLoading, setIsLoading] = useState(true);
  const [product, setProduct] = useState(null); // Initialize product state

  useEffect(() => {
    setIsLoading(true);
    axios.get(`${productUrl}/products/${productId}`)
      .then((response) => {
        console.log(response.data); // Use response.data instead of res.data
        setProduct(response.data); 
        setIsLoading(false);
      })
      .catch((err) => {
        console.log(err);
        setIsLoading(false);
      });
  }, [productId]); // Add productId to dependency array

  return (
    <LayOut>
      {isLoading ? (
        <Loader />
      ) : (
        <ProductCard
          product={product} 
          flex={true}
          renderDesc={true}
        />
      )}
    </LayOut>
  );
}

export default ProductDetail;
