import React, { useEffect, useState } from 'react'; 
import axios from 'axios';
import ProductCard from './ProductCard'; // Ensure this path is correct
import classes from './Product.module.css'; // Ensure this path is correct
import Loader from '../Loader/Loader';

function Product() {
    const [products, setProducts] = useState([]); // Initialize as an empty array
    const [isLoading, setIsLoading] = useState(true); // Start loading as true
    const [error, setError] = useState(null); // Error state

    useEffect(() => {
        setIsLoading(true);
        axios.get("https://fakestoreapi.com/products")
            .then((res) => {
                setProducts(res.data); // Set products data
                setIsLoading(false); // Set loading to false
            })
            .catch((err) => {
                console.log(err);
                setError("Failed to fetch products."); // Set error message
                setIsLoading(false); // Set loading to false
            });
    }, []);

    return (
        <>
            {isLoading ? (
                <Loader />
            ) : error ? (
                <div className={classes.error_message}>{error}</div> // Display error message
            ) : (
                <section className={classes.products_container}>
                    {products.length > 0 ? (
                        products.map(singleProduct => (
                            <ProductCard product={singleProduct} key={singleProduct.id} />
                        ))
                    ) : (
                        <p>No products found.</p> // Handle case with no products
                    )}
                </section>
            )}
        </>
    );
}

export default Product;
