import React, { useEffect, useState } from 'react';
import classes from './ProductDetail.module.css';
import LayOut from '../../Components/LayOut/LayOut'; // Correct path to LayOut component
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { productUrl } from '../../Api/endPoints';
import ProductCard from '../../Components/Product/ProductCard'; // Adjust based on your actual file structure
import Loader from '../../Components/loader/Loader'; // Ensure loader file path is correct

function ProductDetail() {
  const { productId } = useParams();
  const [product, setProduct] = useState(null); // Initialize product state
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state

  useEffect(() => {
    setLoading(true); // Set loading to true before the fetch
    axios.get(`${productUrl}/products/${productId}`)
      .then((response) => {
        setProduct(response.data); // Set the product data
        setLoading(false); // Set loading to false after data is fetched
      })
      .catch((err) => {
        console.log(err);
        setError("Failed to fetch product details."); // Set error message
        setLoading(false); // Set loading to false on error
      });
  }, [productId]); // Add productId as a dependency

  if (loading) {
    return <Loader />; // Show loader while fetching
  }

  if (error) {
    return <p>{error}</p>; // Display error message
  }

  if (!product) {
    return <p>Product not found.</p>; // Handle case where product is not found
  }

  return (
    <LayOut>
      <ProductCard product={product} />
    </LayOut>
  );
}

export default ProductDetail;
