import React, { useEffect, useState } from 'react';
import classes from './Results.module.css';
import LayOut from '../../Components/LayOut/LayOut';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { productUrl } from '../../Api/endPoints';
import ProductCard from '../../Components/Product/ProductCard';

function Results() {
  const { catagoryName } = useParams();
  const [results, setResults] = useState([]); // State for storing results
  const [isLoading, setIsLoading] = useState(true); 
  useEffect(() => {
    axios.get(`${productUrl}/products/category/${catagoryName}`) // Corrected URL interpolation
      .then((res) => {
        setResults(res.data); // Set results to state
        setIsLoading(false)
      })
      .catch((err) => {
       set
      });
  }, []); // Adding catagoryName as a dependency

  return (
    <LayOut>
      <section>
        <h1 style={{ padding: "30px" }}>Results</h1>
        <p style={{ padding: "30px" }}>Category: {catagoryName}</p>
        <hr />
        {isLoading ? (
          <loader/>
        ) : (
        <div className={classes.products_container}>
          {results.length > 0 ? ( // Check if results exist
            results.map((product) => (
              <ProductCard
                key={product.id}
                product={product}
              />
            ))
          ) : (
            <p>No products found in this category.</p> // Message for no results
          )}
        </div>
        )}
      </section>
    </LayOut>
  );
}

export default Results;
