import React, { useEffect, useState } from 'react';
import classes from './ProductDetail.module.css'; // Correct path
import LayOut from '../../Components/LayOut/LayOut';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { productUrl } from '../../Api/endPoints';
import ProductCard from '../../Components/Product/ProductCard';
import Loader from '../../Components/Loader/Loader';


function ProductDetail() {
  const { productId } = useParams();
  const [isLoading, setIsLoading] = useState(true);
  const [product, setProduct] = useState(null);
  const [error, setError] = useState(null); // Error state

  useEffect(() => {
    const fetchProduct = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get(`${productUrl}/products/${productId}`);
        console.log("API Response:", response.data);

        if (response.data && response.data.id) {
          setProduct(response.data);
        } else {
          console.error("Invalid product data structure");
        }
      } catch (error) {
        console.error("Error fetching product:", error);
        setError("Failed to fetch product details."); // Set error message
      } finally {
        setIsLoading(false);
      }
    };

    fetchProduct();
  }, [productId]);

  return (
    <LayOut>
      {isLoading ? (
        <Loader />
      ) : error ? (
        <p>{error}</p> // Display error message
      ) : product ? (
        <ProductCard product={product} flex={true} renderDesc={true} />
      ) : (
        <p>Product not found.</p>
      )}
    </LayOut>
  );
}

export default ProductDetail;
