import React from 'react';
import Rating from '@mui/material/Rating';
import CurrencyFormat from '../CurrencyFormat/CurrencyFormat';
import classes from './Product.module.css';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types'; // For prop validation

function ProductCard({ product, flex, renderDesc }) {
    // Destructure product properties with fallback values
    const { image, title, id, rating, price, description } = product || {};
    
    // Validate product data
    if (!product || !title || !image || !price) {
        return <p>Product information is incomplete.</p>;
    }

    return (
        <div className={`${classes.card_container} ${flex ? classes.product_flexed : ''}`}>
            <Link to={`/products/${id}`}>
                <img src={image} alt={title} className={classes.img_container} />
            </Link>
            <div>
                <h3>{title}</h3>
                {renderDesc && <div style={{ maxWidth: '750px' }}>{description}</div>}
                <div className={classes.rating}>
                    <Rating value={rating?.rate || 0} precision={0.1} readOnly />
                    <small>{rating?.count || 0}</small>
                </div>
                <div>
                    <CurrencyFormat amount={price} />
                </div>
                <button className={classes.button} onClick={() => {/* Add to cart logic */}}>
                    Add to Cart
                </button>
            </div>
        </div>
    );
}

// PropTypes for type checking
ProductCard.propTypes = {
    product: PropTypes.shape({
        image: PropTypes.string.isRequired,
        title: PropTypes.string.isRequired,
        id: PropTypes.string.isRequired,
        rating: PropTypes.shape({
            rate: PropTypes.number,
            count: PropTypes.number,
        }),
        price: PropTypes.number.isRequired,
        description: PropTypes.string,
    }).isRequired,
    flex: PropTypes.bool,
    renderDesc: PropTypes.bool,
};

export default ProductCard;
